import { createRouter, createWebHistory } from 'vue-router';
import SmsForm from '../components/SmsForm.vue';
import SmsTable from '../components/SmsTable.vue';
import SmsAdmin from '../components/SmsAdmin.vue'; // page d’administration

const routes = [{
  path: '/',
  name: 'Formulaire',
  component: SmsForm
}, {
  path: '/messages',
  name: 'Messages',
  component: SmsTable
}, {
  path: '/admin',
  name: 'Administration',
  component: SmsAdmin
}];
const router = createRouter({
  history: createWebHistory(),
  routes
});
export default router;
